    legal: { terms: { title: "Syarat & Ketentuan", lastUpdated: "Terakhir diperbarui", intro: "Dengan mengakses atau menggunakan situs (www.servyard.com) atau aplikasi kami (Android, iOS, Huawei), Anda menyetujui Syarat & Ketentuan ini. Jika tidak setuju, harap jangan gunakan platform kami.", purpose: { title: "1. Tujuan Platform", content: "ServYard adalah platform global yang menghubungkan Penyedia Layanan (\"Penyedia\") dan Pelanggan. ServYard tidak menyediakan, mengawasi, atau menjamin layanan apa pun. Semua komunikasi dan transaksi terjadi antara Penyedia dan Pelanggan." }, userAccounts: { title: "2. Akun Pengguna", points: ["Penyedia wajib memberikan informasi layanan yang jujur dan sah, termasuk lisensi yang valid bila diwajibkan oleh hukum.", "Pelanggan wajib memberikan kontak yang akurat dan hanya menggunakan platform untuk tujuan yang sah.", "Penyedia dan Pelanggan menyetujui perjanjian elektronik yang membebaskan ServYard dari tanggung jawab atas sengketa/kerugian apa pun.", "Pelanggaran Syarat ini dapat mengakibatkan penangguhan atau pengakhiran akun."] }, serviceListings: { title: "3. Daftar Layanan", points: ["Penyedia bertanggung jawab penuh atas keakuratan, legalitas, dan kualitas deskripsi layanan, jadwal, dan harga.", "Dilarang memposting layanan ilegal atau yang dibatasi.", "ServYard berhak menghapus konten yang melanggar hukum atau kebijakan."] }, payments: { title: "4. Pembayaran", points: ["ServYard tidak memproses pembayaran.", "Pembayaran, pengiriman layanan, dan dukungan purna jual ditangani langsung antara Penyedia dan Pelanggan."] }, location: { title: "5. Lokasi", points: ["Pelanggan dapat mengizinkan akses lokasi untuk menampilkan layanan terdekat.", "Data lokasi hanya digunakan untuk meningkatkan relevansi dan tidak dijual ke pihak ketiga."] }, intellectualProperty: { title: "6. Kekayaan Intelektual", points: ["Pengguna hanya boleh mengunggah konten yang mereka miliki atau berhak gunakan secara sah.", "Dengan mempublikasikan, Penyedia memberikan lisensi non-eksklusif kepada ServYard untuk menampilkan konten di platform."] }, liability: { title: "7. Batasan Tanggung Jawab", content: "ServYard tidak bertanggung jawab atas kerusakan, kerugian, atau sengketa yang timbul dari penggunaan platform. Tanggung jawab sepenuhnya ada pada Penyedia dan Pelanggan." }, governingLaw: { title: "8. Hukum yang Berlaku", content: "Syarat ini diatur oleh standar e-commerce internasional dan hukum yang berlaku di negara para pihak." }, acknowledgment: "Dengan menggunakan ServYard, Anda menyatakan telah membaca, memahami, dan menyetujui Syarat & Ketentuan ini." }, privacy: { title: "Kebijakan Privasi", lastUpdated: "Terakhir diperbarui", intro: "ServYard menghormati privasi Anda. Kebijakan ini menjelaskan bagaimana kami mengumpulkan, menggunakan, dan melindungi informasi Anda.", dataCollection: { title: "1. Data yang Dikumpulkan", clients: { title: "Pelanggan:", content: "Nama, kontak, lokasi, kredensial login." }, providers: { title: "Penyedia:", content: "Nama usaha, detail lisensi, kontak, deskripsi layanan, gambar opsional." }, general: { title: "Umum:", content: "Alamat IP, jenis perangkat, browser, cookie." } }, purpose: { title: "2. Tujuan Penggunaan", points: ["Membuat dan mengelola akun pengguna.", "Mencocokkan Pelanggan dan Penyedia berdasarkan lokasi dan preferensi.", "Memastikan kepatuhan terhadap kebijakan platform.", "Menayangkan iklan relevan (Google AdSense/AdMob)."] }, dataSharing: { title: "3. Berbagi Data", intro: "Kami tidak menjual data pribadi. Data hanya dapat dibagikan kepada:", points: ["Otoritas terkait bila diwajibkan oleh hukum.", "Penyedia layanan (hosting, analitik) yang terikat perjanjian kerahasiaan."] }, compliance: { title: "4. Kepatuhan Internasional", content: "ServYard mematuhi GDPR (UE), PDPL (UEA), CCPA (California), dan standar serupa lainnya." }, security: { title: "5. Keamanan Data", content: "Kami menggunakan enkripsi, kontrol akses, dan pemantauan untuk melindungi data Anda. Tidak ada sistem yang 100% aman, namun kami menerapkan langkah-langkah yang sesuai standar industri." }, userRights: { title: "6. Hak Pengguna", intro: "Anda berhak untuk:", points: ["Meminta salinan data Anda.", "Meminta perbaikan atau penghapusan.", "Mencabut persetujuan pemrosesan data."], contact: "Kontak: support@servyard.com" }, acknowledgment: "Dengan menggunakan ServYard, Anda mengakui telah membaca dan memahami Kebijakan Privasi ini." }, disclaimer: { title: "Penafian", lastUpdated: "Terakhir diperbarui", intro: "ServYard adalah platform digital yang netral.", points: ["Kami tidak mendukung, mengendalikan, atau menjamin kualitas, legalitas, keamanan, atau keakuratan layanan yang ditawarkan Penyedia.", "Seluruh tanggung jawab berada pada Penyedia dan Pelanggan.", "Dengan mendaftar, Anda membebaskan ServYard dari klaim, kerusakan, atau sengketa apa pun.", "ServYard berhak menangguhkan atau menghentikan akun yang menyalahgunakan platform."], acknowledgment: "Dengan menggunakan ServYard, Anda menyetujui Penafian ini secara penuh." }, contentPolicy: { title: "Kebijakan Konten", lastUpdated: "Terakhir diperbarui", acceptable: { title: "1. Konten yang Diperbolehkan", content: "Pengguna hanya boleh memposting konten yang akurat, legal, dan saling menghormati." }, prohibited: { title: "2. Konten Terlarang", points: ["Layanan ilegal (narkoba, senjata, perjudian).", "Materi yang ofensif, berbahaya, atau diskriminatif.", "Pernyataan palsu atau menyesatkan.", "Pelanggaran hak kekayaan intelektual."] }, moderation: { title: "3. Moderasi", points: ["ServYard menggunakan alat otomatis dan tinjauan manusia untuk mendeteksi konten terlarang.", "Pelanggaran dapat mengakibatkan penghapusan konten atau penutupan akun."] }, reporting: { title: "4. Pelaporan", content: "Pengguna dapat melaporkan konten yang mencurigakan atau ilegal. ServYard akan menyelidiki dan mengambil tindakan yang diperlukan." }, acknowledgment: "Dengan menggunakan ServYard, Anda setuju untuk mematuhi Kebijakan Konten ini." }, aboutUs: { title: "Tentang Kami", content: ["ServYard adalah pasar digital mutakhir yang mempertemukan pencari layanan dan penyedia di seluruh dunia.", "Misi kami adalah membangun ekosistem tepercaya di mana layanan berkualitas memenuhi kebutuhan nyata, memperkuat komunitas dan peluang ekonomi.", "Baik Anda mencari layanan profesional maupun ingin menawarkan keahlian, ServYard menyediakan platform untuk koneksi yang bermakna."] }, contactUs: { title: "Kontak", website: "Situs web", email: "Email", address: "Alamat" }, providerTerms: { title: "Ketentuan Penyedia", intro: "Dengan membuat akun di ServYard, saya selaku Penyedia menyatakan bahwa:", points: ["Saya bertanggung jawab penuh atas legalitas, kualitas, dan keakuratan layanan saya.", "Saya akan mematuhi semua hukum dan persyaratan lisensi yang berlaku.", "Saya bertanggung jawab atas layanan pelanggan, penjadwalan, penetapan harga, dan pengantaran.", "Saya membebaskan ServYard dari tanggung jawab apa pun yang timbul dari layanan, pelanggan, atau transaksi saya.", "Saya memahami pelanggaran dapat mengakibatkan penangguhan atau pengakhiran akun."] }, customerTerms: { title: "Ketentuan Pelanggan", intro: "Dengan membuat akun di ServYard, saya selaku Pelanggan memahami bahwa:", points: ["ServYard hanyalah platform yang menghubungkan saya dengan penyedia independen.", "Saya bertanggung jawab memverifikasi informasi dan kecocokan Penyedia sebelum bertransaksi.", "Saya memahami ServYard tidak menjamin kualitas, legalitas, atau keamanan layanan.", "Saya membebaskan ServYard dari tanggung jawab terkait penggunaan platform atau transaksi dengan penyedia.", "Saya setuju menggunakan platform hanya untuk tujuan yang sah."] } },
